mapper.select:
  SELECT street.NAME sname,house.id,district.NAME dname,users.NAME uname,type.name tname,house.title,house.DESCRIPTION,house.PRICE,house.PUBDATE,house.FLOORAGE,house.ispass,house.path FROM house LEFT JOIN street ON house.STREET_ID=street.ID
  LEFT JOIN district ON street.DISTRICT_ID=district.ID
  LEFT JOIN TYPE ON type.ID=house.TYPE_ID
  LEFT JOIN users ON users.ID=house.USER_ID
WHERE isdel=0 and ispass=1

  and house.title like concat('%',#{title},'%')


  and district.id=#{did}


  and house.street_id=#{sid}


  and house.type_id=#{tid}


  and house.price >= #{startPrice}


  and house.price <= #{endPrice}
